mapper.insert:
INSERT INTO attendance (
user_id,
input_date
)
VALUES (
#{userId},
#{inputDate}
)

mapper.select:
SELECT COUNT(*) >0
FROM attendance
WHERE user_id = #{userId}
AND input_date = #{inputDate}

mapper.update:
UPDATE attendance
SET
status = #{status},
input_date =
#{inputDate}
WHERE attendance_id = #{attendanceId}
