mapper.insert:
INSERT INTO attendance (
user_id,
attendance_date
)
VALUES (
#{userId},
#{attendanceDate}
)

mapper.select:
SELECT COUNT(*) >0
FROM attendance
WHERE user_id = #{userId}
AND attendance_date = #{attendanceDate}

mapper.update:
UPDATE attendance
SET
status = #{status},
attendance_date =
#{attendanceDate}
WHERE attendance_id = #{attendanceId}

mapper.select:
SELECT a.attendance_id AS attendanceId, u.unit AS unit, u.name AS username, a.status AS status, a.attendance_date AS attendanceDate FROM attendance a JOIN users u ON a.user_id = u.user_id
u.unit = #{unit}
AND u.name = #{username}
AND a.attendance_date = #{date}
